<script setup>
import { computed } from 'vue';
import { useHistoryDataStore } from '../../../stores/store';
import { storeToRefs } from 'pinia';

const historyDataStore = useHistoryDataStore();
const { analyzeData } = storeToRefs(historyDataStore);

const totalPeriods = computed(() => {
   return analyzeData.value.periodCounts.Morning + analyzeData.value.periodCounts.Afternoon + analyzeData.value.periodCounts.Night;
});

const dayPercentage = computed(() => {
   return Math.floor(((analyzeData.value.periodCounts.Morning + analyzeData.value.periodCounts.Afternoon) / totalPeriods.value) * 100);
});

const nightPercentage = computed(() => {
   return Math.floor((analyzeData.value.periodCounts.Night / totalPeriods.value) * 100);
});

const dayOrNight = computed(() => {
   if (dayPercentage.value >= nightPercentage.value) {
      return 'Day';
   } else if (dayPercentage.value < nightPercentage.value) {
      return 'Night';
   } else {
      return 'NAN';
   }
});
</script>
